mapper.update:
update board 
set b_title=#{b_title}, b_content = #{b_content}, b_notice=#{b_notice},category_num=#{category_num} 
where b_num=#{b_num}

update board
set b_delete = 1
where b_num=#{b_num}

update board
set b_hit = b_hit+1
where b_num=#{b_num}

mapper.select:
select NVL(count(m_num),0) cnt from board 

	
		and m_num = #{m_num}
	
	
		and category_num = #{category_num}
	
	and b_delete = 0
